import axios from 'axios';

const API_BASE_URL = '/api/v1';

const apiInstance = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
apiInstance.interceptors.request.use(
  (config) => {
    // Add auth token if available
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor
apiInstance.interceptors.response.use(
  (response) => response.data,
  (error) => {
    console.error('API Error:', error.response || error);

    // Handle specific error cases
    if (error.response) {
      switch (error.response.status) {
        case 401:
          // Handle unauthorized
          break;
        case 404:
          // Handle not found
          break;
        case 500:
          // Handle server error
          break;
        default:
          // Handle other errors
          break;
      }
    }

    return Promise.reject(error.response?.data || error.message);
  }
);

// Price endpoints
export const getPrices = (params = {}) => 
  apiInstance.get('/prices/', { params });

export const getReturns = (params = {}) => 
  apiInstance.get('/prices/returns', { params });

export const getPriceSummary = () => 
  apiInstance.get('/prices/summary');

// Event endpoints
export const getEvents = (params = {}) => 
  apiInstance.get('/events/', { params });

export const getEventImpact = (params = {}) => 
  apiInstance.get('/events/impact', { params });

// Change point endpoints
export const getChangePoints = () => 
  apiInstance.get('/changepoint/');

export const getChangePointImpact = (date, params = {}) => 
  apiInstance.get(`/changepoint/${date}/impact`, { params });

// Analysis endpoints
export const getAnalysisSummary = () => 
  apiInstance.get('/analysis/summary');

export const getEventCorrelation = () => 
  apiInstance.get('/analysis/correlation');

// Health check
export const checkHealth = () => 
  apiInstance.get('/health');

export const api = apiInstance;
export { apiInstance };
export default apiInstance;
