// File: src/dashboard/frontend/src/App.js
import React, { useState, useEffect } from 'react';
import {
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid,
  Tooltip, Legend, ResponsiveContainer, ComposedChart, Area,
  ScatterChart, Scatter, PieChart, Pie, Cell
} from 'recharts';
import {
  Container, Row, Col, Card, CardBody, CardTitle, CardText,
  Form, FormGroup, Label, Input, Button, Alert, Navbar, Nav,
  NavItem, TabContent, TabPane, NavLink, Spinner
} from 'reactstrap';
import './App.css';

// API base URL
const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState({
    prices: [],
    events: [],
    analysis: null,
    changePoints: [],
    volatility: null,
    correlations: []
  });
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [eventImpact, setEventImpact] = useState(null);
  const [filters, setFilters] = useState({
    startDate: '2000-01-01',
    endDate: '2022-12-31',
    eventType: 'all',
    windowDays: 30
  });
  const [activeTab, setActiveTab] = useState('dashboard');

  // Fetch all data on component mount
  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    setLoading(true);
    try {
      // Fetch prices
      const priceRes = await fetch(
        `${API_BASE_URL}/prices?start_date=${filters.startDate}&end_date=${filters.endDate}`
      );
      const priceData = await priceRes.json();

      // Fetch events
      const eventRes = await fetch(`${API_BASE_URL}/events`);
      const eventData = await eventRes.json();

      // Fetch analysis summary
      const analysisRes = await fetch(`${API_BASE_URL}/analysis/summary`);
      const analysisData = await analysisRes.json();

      // Fetch change points
      const cpRes = await fetch(`${API_BASE_URL}/analysis/changepoints`);
      const cpData = await cpRes.json();

      // Fetch volatility
      const volRes = await fetch(`${API_BASE_URL}/analysis/volatility?window=30`);
      const volData = await volRes.json();

      // Fetch correlations
      const corrRes = await fetch(`${API_BASE_URL}/analysis/correlation`);
      const corrData = await corrRes.json();

      setData({
        prices: priceData,
        events: eventData,
        analysis: analysisData,
        changePoints: cpData.change_points || [],
        volatility: volData,
        correlations: corrData.correlations || []
      });
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEventClick = async (event) => {
    setSelectedEvent(event);
    try {
      const response = await fetch(
        `${API_BASE_URL}/analysis/event-impact/${encodeURIComponent(event.title)}?window_days=${filters.windowDays}`
      );
      const impactData = await response.json();
      setEventImpact(impactData);
    } catch (error) {
      console.error('Error fetching event impact:', error);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const applyFilters = () => {
    fetchAllData();
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatNumber = (num) => {
    return new Intl.NumberFormat('en-US', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(num);
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <Spinner color="primary" />
        <p>Loading Brent Oil Price Analysis Dashboard...</p>
      </div>
    );
  }

  return (
    <div className="App">
      {/* Navigation */}
      <Navbar color="dark" dark expand="md">
        <Container>
          <Navbar href="/" className="navbar-brand">
            Brent Oil Analysis Dashboard
          </Navbar>
          <Nav className="ml-auto" navbar>
            <NavItem>
              <NavLink
                className={activeTab === 'dashboard' ? 'active' : ''}
                onClick={() => setActiveTab('dashboard')}
              >
                Dashboard
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={activeTab === 'analysis' ? 'active' : ''}
                onClick={() => setActiveTab('analysis')}
              >
                Bayesian Analysis
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={activeTab === 'events' ? 'active' : ''}
                onClick={() => setActiveTab('events')}
              >
                Events Impact
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={activeTab === 'volatility' ? 'active' : ''}
                onClick={() => setActiveTab('volatility')}
              >
                Volatility
              </NavLink>
            </NavItem>
          </Nav>
        </Container>
      </Navbar>

      {/* Filters */}
      <Container className="mt-4">
        <Card>
          <CardBody>
            <Row>
              <Col md={3}>
                <FormGroup>
                  <Label for="startDate">Start Date</Label>
                  <Input
                    type="date"
                    name="startDate"
                    id="startDate"
                    value={filters.startDate}
                    onChange={handleFilterChange}
                  />
                </FormGroup>
              </Col>
              <Col md={3}>
                <FormGroup>
                  <Label for="endDate">End Date</Label>
                  <Input
                    type="date"
                    name="endDate"
                    id="endDate"
                    value={filters.endDate}
                    onChange={handleFilterChange}
                  />
                </FormGroup>
              </Col>
              <Col md={3}>
                <FormGroup>
                  <Label for="eventType">Event Type</Label>
                  <Input
                    type="select"
                    name="eventType"
                    id="eventType"
                    value={filters.eventType}
                    onChange={handleFilterChange}
                  >
                    <option value="all">All Types</option>
                    {data.events.type_distribution && 
                      Object.keys(data.events.type_distribution).map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))
                    }
                  </Input>
                </FormGroup>
              </Col>
              <Col md={3} className="d-flex align-items-end">
                <Button color="primary" onClick={applyFilters}>
                  Apply Filters
                </Button>
              </Col>
            </Row>
          </CardBody>
        </Card>
      </Container>

      {/* Main Content */}
      <Container className="mt-4">
        <TabContent activeTab={activeTab}>

          {/* Dashboard Tab */}
          <TabPane tabId="dashboard">
            <Row>
              <Col md={12}>
                <Alert color="info">
                  Run backend: <code>cd dashboard/backend && python app.py</code> 
                  Run frontend: <code>cd dashboard/frontend && npm start</code>
                </Alert>
              </Col>
            </Row>
            <Row>
              {/* Key Statistics */}
              <Col md={3}>
                <Card className="stat-card">
                  <CardBody>
                    <CardTitle tag="h6">Average Price</CardTitle>
                    <CardText tag="h4">
                      ${formatNumber(data.prices.stats?.mean || 0)}
                    </CardText>
                  </CardBody>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="stat-card">
                  <CardBody>
                    <CardTitle tag="h6">Price Range</CardTitle>
                    <CardText tag="h4">
                      ${formatNumber(data.prices.stats?.min || 0)} - ${formatNumber(data.prices.stats?.max || 0)}
                    </CardText>
                  </CardBody>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="stat-card">
                  <CardBody>
                    <CardTitle tag="h6">Total Events</CardTitle>
                    <CardText tag="h4">
                      {data.events.count || 0}
                    </CardText>
                  </CardBody>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="stat-card">
                  <CardBody>
                    <CardTitle tag="h6">Volatility</CardTitle>
                    <CardText tag="h4">
                      {formatNumber(data.volatility?.average_volatility || 0)}
                    </CardText>
                  </CardBody>
                </Card>
              </Col>
            </Row>

            {/* Price Chart */}
            <Row className="mt-4">
              <Col md={8}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Brent Oil Price History</CardTitle>
                    <div style={{ height: 400 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <ComposedChart data={data.prices.dates?.map((date, i) => ({
                          date: formatDate(date),
                          price: data.prices.prices[i],
                          fullDate: date
                        }))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="date" angle={-45} textAnchor="end" height={60} />
                          <YAxis label={{ value: 'Price (USD)', angle: -90, position: 'insideLeft' }} />
                          <Tooltip 
                            labelFormatter={(value) => `Date: ${value}`}
                            formatter={(value) => [`$${formatNumber(value)}`, 'Price']}
                          />
                          <Area 
                            type="monotone" 
                            dataKey="price" 
                            stroke="#8884d8" 
                            fill="#8884d8" 
                            fillOpacity={0.3}
                          />
                          <Line 
                            type="monotone" 
                            dataKey="price" 
                            stroke="#8884d8" 
                            strokeWidth={2}
                            dot={false}
                          />
                        </ComposedChart>
                      </ResponsiveContainer>
                    </div>
                  </CardBody>
                </Card>
              </Col>

              {/* Event Timeline */}
              <Col md={4}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Recent Events</CardTitle>
                    <div className="events-timeline">
                      {data.events.events?.slice(0, 5).map((event, index) => (
                        <div 
                          key={index} 
                          className="event-item"
                          onClick={() => handleEventClick(event)}
                        >
                          <div className="event-date">{formatDate(event.date)}</div>
                          <div className="event-title">{event.title}</div>
                          <div className="event-type">{event.type}</div>
                        </div>
                      ))}
                    </div>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </TabPane>

          {/* Bayesian Analysis Tab */}
          <TabPane tabId="analysis">
            <Row>
              <Col md={8}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Change Point Analysis</CardTitle>
                    {data.changePoints.length > 0 ? (
                      <div>
                        <Alert color="info">
                          <strong>Detected Change Point:</strong> {formatDate(data.changePoints[0].date)}
                          <br />
                          <strong>95% HDI:</strong> {formatDate(data.changePoints[0].hdi_95[0])} to {formatDate(data.changePoints[0].hdi_95[1])}
                          <br />
                          <strong>Posterior Probability:</strong> {(data.changePoints[0].probability * 100).toFixed(1)}%
                        </Alert>

                        <div className="parameter-changes mt-4">
                          <h6>Parameter Changes:</h6>
                          <Row>
                            <Col md={6}>
                              <Card className="parameter-card">
                                <CardBody>
                                  <CardTitle tag="h6">Mean Returns</CardTitle>
                                  <CardText>
                                    Change: {formatNumber(data.changePoints[0].parameter_changes?.mean_change?.mean || 0)}
                                    <br />
                                    Probability of Increase: {(data.changePoints[0].parameter_changes?.mean_change?.probability_positive * 100).toFixed(1)}%
                                  </CardText>
                                </CardBody>
                              </Card>
                            </Col>
                            <Col md={6}>
                              <Card className="parameter-card">
                                <CardBody>
                                  <CardTitle tag="h6">Volatility</CardTitle>
                                  <CardText>
                                    Change: {formatNumber(data.changePoints[0].parameter_changes?.volatility_change?.mean || 0)}
                                    <br />
                                    Probability of Increase: {(data.changePoints[0].parameter_changes?.volatility_change?.probability_increase * 100).toFixed(1)}%
                                  </CardText>
                                </CardBody>
                              </Card>
                            </Col>
                          </Row>
                        </div>
                      </div>
                    ) : (
                      <Alert color="warning">
                        No change points detected or analysis not available.
                      </Alert>
                    )}
                  </CardBody>
                </Card>
              </Col>

              <Col md={4}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Nearby Events</CardTitle>
                    {data.changePoints.length > 0 && data.changePoints[0].nearby_events?.length > 0 ? (
                      <div className="nearby-events">
                        {data.changePoints[0].nearby_events.map((event, index) => (
                          <div key={index} className="nearby-event">
                            <div className="event-title">{event.title}</div>
                            <div className="event-details">
                              {formatDate(event.date)} ({event.days_from_cp} days)
                            </div>
                            <div className="event-type">{event.type}</div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p>No events found near the change point.</p>
                    )}
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </TabPane>

          {/* Events Impact Tab */}
          <TabPane tabId="events">
            <Row>
              <Col md={8}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Event Impact Analysis</CardTitle>
                    <div style={{ height: 400 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <ScatterChart>
                          <CartesianGrid />
                          <XAxis 
                            type="category" 
                            dataKey="date" 
                            name="Date"
                            angle={-45}
                            textAnchor="end"
                            height={60}
                          />
                          <YAxis 
                            type="number" 
                            dataKey="price_change" 
                            name="Price Change %" 
                            label={{ value: 'Price Change (%)', angle: -90, position: 'insideLeft' }}
                          />
                          <Tooltip 
                            formatter={(value, name, props) => {
                              if (name === 'price_change') return [`${value.toFixed(2)}%`, 'Change'];
                              return [value, name];
                            }}
                            labelFormatter={(label) => `Date: ${formatDate(label)}`}
                          />
                          <Legend />
                          <Scatter 
                            name="Event Impacts" 
                            data={data.correlations} 
                            fill="#8884d8"
                            onClick={(data) => handleEventClick(data.payload)}
                          />
                        </ScatterChart>
                      </ResponsiveContainer>
                    </div>
                  </CardBody>
                </Card>
              </Col>

              <Col md={4}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Top Impact Events</CardTitle>
                    <div className="impact-ranking">
                      {data.correlations
                        .sort((a, b) => Math.abs(b.price_change) - Math.abs(a.price_change))
                        .slice(0, 5)
                        .map((event, index) => (
                          <div 
                            key={index} 
                            className="impact-item"
                            onClick={() => handleEventClick(event)}
                          >
                            <div className="impact-rank">{index + 1}</div>
                            <div className="impact-details">
                              <div className="impact-title">{event.event}</div>
                              <div className={`impact-change ${event.price_change > 0 ? 'positive' : 'negative'}`}>
                                {event.price_change > 0 ? '+' : ''}{event.price_change.toFixed(1)}%
                              </div>
                            </div>
                          </div>
                        ))
                      }
                    </div>
                  </CardBody>
                </Card>
              </Col>
            </Row>

            {/* Event Impact Details */}
            {selectedEvent && eventImpact && (
              <Row className="mt-4">
                <Col md={12}>
                  <Card>
                    <CardBody>
                      <CardTitle tag="h5">Impact Analysis: {selectedEvent.title}</CardTitle>
                      <Row>
                        <Col md={4}>
                          <Card className="impact-card">
                            <CardBody>
                              <CardTitle tag="h6">Price Change</CardTitle>
                              <CardText 
                                className={`impact-value ${eventImpact.impact.percent_change > 0 ? 'positive' : 'negative'}`}
                              >
                                {eventImpact.impact.percent_change > 0 ? '+' : ''}
                                {eventImpact.impact.percent_change.toFixed(2)}%
                              </CardText>
                              <CardText>
                                ${formatNumber(eventImpact.impact.pre_avg)}  ${formatNumber(eventImpact.impact.post_avg)}
                              </CardText>
                            </CardBody>
                          </Card>
                        </Col>
                        <Col md={8}>
                          <div style={{ height: 300 }}>
                            <ResponsiveContainer width="100%" height="100%">
                              <LineChart data={eventImpact.price_data.dates.map((date, i) => ({
                                date: formatDate(date),
                                price: eventImpact.price_data.prices[i],
                                isEvent: date === selectedEvent.date
                              }))}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="date" />
                                <YAxis />
                                <Tooltip />
                                <Line 
                                  type="monotone" 
                                  dataKey="price" 
                                  stroke="#8884d8" 
                                  strokeWidth={2}
                                  dot={(props) => {
                                    if (props.payload.isEvent) {
                                      return <circle cx={props.cx} cy={props.cy} r={4} fill="#ff0000" />;
                                    }
                                    return null;
                                  }}
                                />
                              </LineChart>
                            </ResponsiveContainer>
                          </div>
                        </Col>
                      </Row>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            )}
          </TabPane>

          {/* Volatility Tab */}
          <TabPane tabId="volatility">
            <Row>
              <Col md={8}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Volatility Analysis</CardTitle>
                    <div style={{ height: 400 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <LineChart data={data.volatility?.volatility_series?.dates.map((date, i) => ({
                          date: formatDate(date),
                          volatility: data.volatility.volatility_series.volatility[i]
                        }))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="date" angle={-45} textAnchor="end" height={60} />
                          <YAxis label={{ value: 'Volatility', angle: -90, position: 'insideLeft' }} />
                          <Tooltip />
                          <Line 
                            type="monotone" 
                            dataKey="volatility" 
                            stroke="#82ca9d" 
                            strokeWidth={2}
                            dot={false}
                          />
                          <Area 
                            type="monotone" 
                            dataKey="volatility" 
                            stroke="#82ca9d" 
                            fill="#82ca9d" 
                            fillOpacity={0.3}
                          />
                        </LineChart>
                      </ResponsiveContainer>
                    </div>
                  </CardBody>
                </Card>
              </Col>

              <Col md={4}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">Event Volatility Impact</CardTitle>
                    <div style={{ height: 300 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <BarChart data={data.volatility?.event_volatility?.slice(0, 5).map(event => ({
                          name: event.event.length > 15 ? event.event.substring(0, 15) + '...' : event.event,
                          volatilityChange: event.volatility_change || 0
                        }))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="volatilityChange" fill="#8884d8" />
                        </BarChart>
                      </ResponsiveContainer>
                    </div>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </TabPane>
        </TabContent>
      </Container>

      {/* Footer */}
      <footer className="footer mt-4">
        <Container>
          <p className="text-center text-muted">
            Brent Oil Price Analysis Dashboard  Bayesian Change Point Detection  {new Date().getFullYear()}
          </p>
        </Container>
      </footer>
    </div>
  );
}

export default App;
