import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api/v1';

const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
apiClient.interceptors.request.use(
  (config) => {
    // Add auth token if available
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor
apiClient.interceptors.response.use(
  (response) => response.data,
  (error) => {
    console.error('API Error:', error.response || error);

    const message =
      error?.response?.data?.error ||
      error?.response?.data?.message ||
      error?.message ||
      'Request failed';
    return Promise.reject({ message });
  }
);

// Price endpoints
export const getPrices = (params = {}) => 
  apiClient.get('prices/', { params });

export const getReturns = (params = {}) => 
  apiClient.get('prices/returns', { params });

export const getPriceSummary = () => 
  apiClient.get('prices/summary');

// Event endpoints
export const getEvents = (params = {}) => 
  apiClient.get('events/', { params });

export const getEventImpact = (params = {}) => 
  apiClient.get('events/impact', { params });

// Change point endpoints
export const getChangePoints = () => 
  apiClient.get('changepoint/');

export const getChangePointImpact = (date, params = {}) => 
  apiClient.get(`changepoint/${date}/impact`, { params });

// Analysis endpoints
export const getAnalysisSummary = () => 
  apiClient.get('analysis/summary');

export const getEventCorrelation = () => 
  apiClient.get('analysis/correlation');

// Health check
export const checkHealth = () => 
  axios.get('http://localhost:5000/health');

export const api = {
  getPrices,
  getReturns,
  getPriceSummary,
  getEvents,
  getEventImpact,
  getChangePoints,
  getChangePointImpact,
  getAnalysisSummary,
  getEventCorrelation,
  checkHealth
};

export { apiClient };
export default api;
